
public class ReverseStr {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String a="shenbu";
		String org="";
		for(int i=a.length()-1;i>=0;i--)
		{
			char c = a.charAt(i);
			org=org+c;
		}
		System.out.println(org);
	}

}


